    private void btnIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIgualActionPerformed

        if (txtResultado.getText().isEmpty()) {
          
        } else {

            valorB = Double.parseDouble(txtResultado.getText());

            switch (opcao) {
                case "+":
                    txtResultado.setText(String.valueOf(valorA + valorB));
                    break;
                case "-":
                    txtResultado.setText(String.valueOf(valorA - valorB));
                    break;
                case "x":
                    txtResultado.setText(String.valueOf(valorA * valorB));
                    break;
                case "/":
                                
                try {

                    if (valorA == 0 && valorB == 0) {
                        txtResultado.setText("Resultado Indefinido");
                    } else {
                        txtResultado.setText(String.valueOf(valorA / valorB));
                    }
                } catch (ArithmeticException e) {

                }

                case "raiz":

                    txtResultado.setText(String.valueOf(Math.sqrt(raizQuadrada)));
                    break;

                default:

            }

        }


    }//GEN-LAST:event_btnIgualActionPerformed

