    private void btnPiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPiActionPerformed

        //validando se o campo txtResultado é vazio. Caso seja vazio o botão pressionado não fará nada.
        
         pi = Math.PI;
        if (txtResultado.getText().isEmpty()) {
           
            txtResultado.setText(String.valueOf(pi));

        } else {

           
            txtResultado.setText(String.valueOf(pi));

            if (opcao == "+") {
                txtResultado.setText("");

                valorA = pi;
            } else if (opcao == "-") {
                txtResultado.setText("");
                valorA = pi;
            } else if (opcao == "x") {
                txtResultado.setText("");
                valorA = pi;

            } else if (opcao == "/") {
                txtResultado.setText("");
                valorA = pi;
            }
        }


    }//GEN-LAST:event_btnPiActionPerformed

    private void btnRaizQuadradaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRaizQuadradaActionPerformed

        //Se o botão raiz for pressionado sem valor ele seta o valor zero no txtResultado.
        if (txtResultado.getText().isEmpty()) {

            txtResultado.setText(String.valueOf("0"));

        } else {

            raizQuadrada = Double.parseDouble(txtResultado.getText());

            opcao = "raiz";

        }

    }//GEN-LAST:event_btnRaizQuadradaActionPerformed

